package priscillaString;

public class ExemploString1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String x = new String("LP2");
		String y = new String("LP2");
		if (x==y) // vai dar que são diferentes, pois quando uso o "==", compara os endereços, as referencias.
		{
			System.out.println("São iguais");
		}
		else
		{
				System.out.println("São diferentes");
		
	}}}
